import React from 'react';
import {ImmutablePropTypes, classNames, PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';
import ReferenceObject from './ReferenceObject';

export default class Attachments extends React.PureComponent {
    render() {
        const {activityEntry, onDelete} = this.props;
        const attachments = activityEntry.get('attachments');
        return (
            <ul className={classNames(prefixNS('attachments'), 'list-unstyled')}>
                {attachments.get('attachments').map(attachment =>
                    <li key={attachment.get('cdb_object_id')}>
                        <ReferenceObject referenceObject={attachment}
                                         onDelete={onDelete}/>
                    </li>
                )}
            </ul>
        );
    }
}

Attachments.propTypes = {
    activityEntry: ImmutablePropTypes.map.isRequired,
    onDelete: PropTypes.func
};
