import React from 'react';
import Attachments from './Attachments';
import {prefixNS} from '../helpers';
import {ImmutablePropTypes} from 'cs-web-components-externals';

export default class Footer extends React.PureComponent {
    render() {
        const {activityEntry} = this.props;
        return (
            <div className={prefixNS('footer')}>
                <Attachments activityEntry={activityEntry}/>
                <div className={prefixNS('footer-actions')}>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

Footer.propTypes = {
    activityEntry: ImmutablePropTypes.map.isRequired
};
