import React from 'react';
import {Navigation} from 'cs-web-components-base';
import {ImmutablePropTypes} from 'cs-web-components-externals';

export default function PostingAuthor(props) {
    const {posting} = props;
    const person = posting.get('author');
    const link = person.get('system:ui_link');
    const name = person.get('name');
    if (link) {
        return (
            <Navigation.Link to={link}>
                {name}
            </Navigation.Link>
        );
    }
    return (
        <span>{name}</span>
    );
}

PostingAuthor.propTypes = {
    posting: ImmutablePropTypes.map.isRequired
};
