import React from 'react';
import PersonPicture from './PersonPicture';
import Footer from './Footer';
import {classNames, ImmutablePropTypes, PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';

const Title = ({children}) => (
    <div className={prefixNS('posting-title')}>
        {children}
    </div>
);

const Nav = ({children}) => (
    <nav className={prefixNS('posting-nav')}>
        {children}
    </nav>
);

export default class PostingLayout extends React.Component {
    render() {
        const {posting, className} = this.props;
        const author = posting.get('author');
        return (
            <div className={classNames(className, prefixNS('posting-layout'))}>
                <div className={prefixNS('posting-icon')}>
                    <PersonPicture person={author} />
                </div>
                <div className={prefixNS('posting-detail')}>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

PostingLayout.propTypes = {
    posting: ImmutablePropTypes.map.isRequired,
    className: PropTypes.string
};

PostingLayout.Title = Title;
PostingLayout.Nav = Nav;
PostingLayout.Footer = Footer;
