import {getAppSetup} from 'cs-web-components-base';
import Immutable from 'immutable';

const defaultSetups = Immutable.fromJS({
    topicURL: '/internal/activitystream/topic',
    excludeTopicClasses: [
        'cdbblog_posting', 'cdbblog_user_posting',
        'cdbblog_system_posting', 'cdbblog_comment']
});

const APP_SETUPS = getAppSetup().get(componentNameSpace, defaultSetups);

export const CHANNLE_LINK = APP_SETUPS.get('channelURL');

export const SUBSCRIPTION_LINK = APP_SETUPS.get('subscriptionURL');

export const TOPIC_LINK = APP_SETUPS.get('topicURL');

export const STANDALONE_INSTANCE = 'standalone';

export const EXCLUDE_TOPIC_CLASSES = APP_SETUPS.get('excludeTopicClasses');
