import React from 'react';
import AppMain from './AppMain';
import Thread from './Thread';
import {ReactRouter, browserHistory} from 'cs-web-components-externals';
import {APPBASE} from '../helpers';

export default class App extends React.Component {
    render() {
        const postingPath = `${APPBASE}/posting/:postingID`;
        return (
            <ReactRouter.Router history={browserHistory}>
                <ReactRouter.Switch>
                    <ReactRouter.Route path={postingPath} component={Thread}/>
                    <ReactRouter.Route component={AppMain}/>
                </ReactRouter.Switch>
            </ReactRouter.Router>
        );
    }
}
