import React from 'react';
import {storiesOf} from 'cs-web-components-storybook';
import {getAppContainer, Registry} from 'cs-web-components-base';
import Immutable from 'immutable';

const mockObj = Immutable.fromJS({
    cdb_object_id: '8a3dd90f-f733-11e5-91ee-28d244545361'
});

const instanceName = 'cs-activitystream-story#1';

const Container = getAppContainer();
const ObjectActivities = Registry.findComponent('cs-activitystream-web-ObjectActivities');

storiesOf('ObjectActivities', module)  // eslint-disable-line no-undef
    .add('default', () => (
        <Container>
            <ObjectActivities
                instanceName={instanceName}
                contextObject={mockObj} />
        </Container>
    ));

export default {};
