import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import {Row, Col} from 'react-bootstrap';
import {prefixNS} from '../helpers';

export default class AppRow extends React.Component {

    render() {
        const {additionalClassNames} = this.props;
        const rowClasses = (
            additionalClassNames ? prefixNS("app-row ") + additionalClassNames : prefixNS("app-row")
        );

        return (
            <Row className={rowClasses} id={this.props.id}>
                <Col xs={0} sm={1} md={2}
                     className={prefixNS("share-objects-app-gutter-left")} />

                <Col xs={1}
                     className={prefixNS("share-objects-app-col-left")}>
                    {this.props.left}
                </Col>

                {/* do not use full width in large viewports */}
                <Col xs={10} sm={8} md={6} lg={5}
                     className={prefixNS("share-objects-app-col-center")}>
                    {this.props.center}
                </Col>

                <Col xs={1}
                     className={prefixNS("share-objects-app-col-right")}>
                    {this.props.right}
                </Col>

                <Col xs={0} sm={1} md={2} lg={3}
                     className={prefixNS("share-objects-app-gutter-right")} />
            </Row>
        );
    }
}

AppRow.propTypes = {
    additionalClassNames: PropTypes.string,
    id: PropTypes.string,
    left: PropTypes.element,
    center: PropTypes.element,
    right: PropTypes.element
};
