import React from 'react';
import {SVGIcon} from 'cs-web-components-base';
import {formatStr} from '../i18n';
import {prefixNS} from '../helpers';
import * as constants from '../constants';


export default class Success extends React.Component {

    render() {
        return (
            <div className={prefixNS("success-message")}>
                <SVGIcon name={constants.ICON_INFO}
                         size="sm" />
                <span>&nbsp;</span>
                {formatStr(constants.SUCCESS_MESSAGE)}
                <span>&nbsp;</span>
                {window.appSetup['cs-sharing'].is_cdbpc ?
                    formatStr(constants.SUCCESS_CLOSE) : null}
            </div>
        );
    }
}
