import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../../helpers';

export default class RecipientImage extends React.Component {

    render() {
        const {img_url, imgName} = this.props;
        const imgPath = img_url ? img_url : "/resources/icons/byname/" + imgName;
        return (
            <div className={prefixNS("recipients-img-wrapper")}
                 style={{
                     backgroundImage: "url('" + imgPath + "')"
                 }}>
            </div>
        );
    }
}

RecipientImage.propTypes = {
    img_url: PropTypes.string,
    imgName: PropTypes.string
};
