import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import RecipientRoleRenderer from './RecipientRoleRenderer';
import RecipientUserRenderer from './RecipientUserRenderer';
import PersonalSharingGroupRenderer from './PersonalSharingGroupRenderer';
import {getRecipientClass} from '../../helpers';

export default class RecipientItemRender extends React.Component {

    render() {
        const recipient = this.props.item;
        const recipientClass = getRecipientClass(recipient);

        switch (recipientClass) {
            case "angestellter":
                return <RecipientUserRenderer {...this.props} />;
            case "cdb_global_role":
                return <RecipientRoleRenderer {...this.props} />;
            case "cdb_personal_sharing_group":
                return <PersonalSharingGroupRenderer {...this.props} />;
        }

        return null;
    }
}

RecipientItemRender.propTypes = {
    item: ImmutablePropTypes.map
};
