import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import RecipientImage from './RecipientImage';
import * as constants from '../../constants';
import {prefixNS} from '../../helpers';

export default class RecipientUserRender extends React.Component {

    getRoleDescriptionAttribute() {
        const {language} = window.appSetup['cs-sharing'];

        if (language === "de") {
            return "description";
        }

        return "description_ml_" + language;
    }

    render() {
        const recipient = this.props.item;
        return (
            <div className={prefixNS("recipients-role")}>
                <RecipientImage imgName={constants.ICON_COMMON_ROLE} />

                <ul className={prefixNS("recipients-recipient-data")}>
                    <li>
                        <strong>
                            {recipient.get(constants.DESC)}
                        </strong>
                    </li>

                    <li className={prefixNS("text-muted")}>
                        {recipient.get(this.getRoleDescriptionAttribute())}
                    </li>
                </ul>
            </div>
        );
    }
}

RecipientUserRender.propTypes = {
    item: ImmutablePropTypes.map
};
