import Provider from './Provider';

/**
 * Transform a normal table to a tree table via
 * extracting hierarchical information from the
 * rows.
 *
 * React Properties
 * ----------------
 * This feature defines some more props for the table component:
 *
 * +-----------------------+----------------+---------+-------------------------------------------+
 * | Property              | Type           | Default | Use                                       |
 * +=======================+================+=========+===========================================+
 * | treeIDColumnID        | string         | \-      | Which column contains the id of the       |
 * |                       |                |         | current node in the tree                  |
 * +-----------------------+----------------+---------+-------------------------------------------+
 * | treeParentColumnID    | string         | \-      | Which column contains the id of the       |
 * |                       |                |         | parent node in the tree                   |
 * +-----------------------+----------------+---------+-------------------------------------------+
 *
 * @module
 */
export default {
    Provider
};
