import React from 'react';
import {SinglePage, PageFrame, isCEDesktop} from 'cs-web-components-base';
import CreateLink from '../components/CreateLink';
import {prefixNS} from '../helpers';


class CEWrapper extends React.Component {
    render() {
        return (
            <div className={'cs-web-components-base-application-frame'}>
                <div className={prefixNS('application-pagecontent')}>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

export default class MainComponent extends React.Component {
    render() {
        if (isCEDesktop()) {
            return (<SinglePage frameComponent={CEWrapper}
                                pageContent={CreateLink} />);
        }
        else {
            return (<SinglePage frameComponent={PageFrame.ApplicationFrame}
                                pageContent={CreateLink} />);
        }
    }
}
