from cdb import conffile_reader, misc

conf_file = None


def get_conf_file_value(key, default=None):
    global conf_file
    ret = default
    if not conf_file:
        try:
            conf_file = conffile_reader.conffile_reader("officelink.conf")
            ret = conf_file.get_conf_param(key, default)
        except IOError:
            misc.log_error("Config file 'etc/officelink.conf' not found!")
    return ret
