"""
Widget plugin for cs.pcs.dashboard.
"""

__docformat__ = "restructuredtext en"
__revision__ = "$Id: projectteam.py 95589 2013-03-07 15:46:37Z yzh $"

from cdb import elink
from cdb import sig

__all__ = []


@elink.using_template_engine("chameleon")
class PluginImpl(elink.Application):

    __plugin_macro_file__ = "widget_projectteam.html"


# lazy initialization
app = None


@sig.connect("cs.pcs.dashboard.getplugins")
def get_plugin():
    global app
    if app is None:
        app = PluginImpl()
    return (7, app)
