from cdb import sig
from cdb import elink
from cdb import util
from cdb.objects.rules import Rule
from cs.pcs.projects import Project
from cs.activitystream.rest_app.model import SubscriptionCategoryBase


__all__ = []


@elink.using_template_engine("chameleon")
class ActivityStreamPlugin(elink.Application):
    pass


# lazy initialization
app = None


@sig.connect(Project, "cs.activitystream.getplugins")
def get_plugin():
    global app
    if app is None:
        app = ActivityStreamPlugin()
    return app


PROJECT_RULE = "cdbpcs: Kosmodrom: My Objects"


class MyProjectSubscriptions(SubscriptionCategoryBase):

    priority = 1

    @property
    def title(self):
        return util.Labels()["pcs_projects"]

    def get_objects(self):
        rule = Rule.ByKeys(PROJECT_RULE)
        if rule:
            return [proj for proj in rule.getObjects(
                Project, add_expr=self.additional_condition, order_by="project_name")
                    if proj.CheckAccess("read")]
        return []
