import os
from cdb import rte
from cdb import sig
from cs.platform.web import static


COMPONENT = "cs-pcs-projects-web"


@sig.connect(rte.APPLICATIONS_LOADED_HOOK)
def _register_libraries():
    lib = static.Library(
        COMPONENT,
        "15.1.0",
        os.path.join(os.path.dirname(__file__), 'js', 'build'))
    lib.add_file("%s.js" % COMPONENT)
    lib.add_file("%s.js.map" % COMPONENT)
    static.Registry().add(lib)
