from cdb import sqlapi


class UpdateDocTemplateRelations(object):
    def run(self):
        tmpl_tables = ["cdbpcs_task2doctmpl", "cdbpcs_prj2doctmpl", "cdbpcs_cl2doctmpl", "cdbpcs_cli2doctmpl"]
        for t in tmpl_tables:
            sqls = u"{0} set use_selected_index=1 where use_selected_index  not in (0, 1)".format(t)
            sqlapi.SQLupdate(sqls)


post = [UpdateDocTemplateRelations]
