function resizeTable() {
	    var reportwin = $("#report_window");
	    var titlediv = $("div#elink_page_title");
	    var newheight = document.documentElement.clientHeight
	                    - titlediv.outerHeight(true)
	                    - 20;

	    try {
	    	reportwin.height(newheight);
	    }catch(e){};
}

$(document).ready(function() {
	resizeTable();
	$("#report_window").show();
	var pdfurl = $("#report_window").attr("src") + "#view=Fit&toolbar=0&statusbar=0&navpanes=0";
	$("#myframe").attr("src", pdfurl);
});

function downloadXLS() {
	$("#downloadme").attr("src", $("#excel").attr("xlshref"));
}


