import * as constants from '../../constants';
import {prefixNS} from '../../helpers';

export const GET_TASK_DETAILS = prefixNS('GET_TASK_DETAILS');
export const TASK_DETAILS = prefixNS('TASK_DETAILS');
export const RESET_TASK_DETAILS = prefixNS('RESET_TASK_DETAILS');
export const SELECT_FILE_CONTAINER = prefixNS('SELECT_FILE_CONTAINER');

export function getTaskDetails(task_object_id) {
    return {
        type: GET_TASK_DETAILS,
        payload: {task_object_id}
    };
}

export function resetTaskDetails() {
    return {
        type: RESET_TASK_DETAILS
    };
}

export function selectFileContainer(fileContainerID) {
    return {
        type: SELECT_FILE_CONTAINER,
        payload: {
            [constants.ID]: fileContainerID
        }
    };
}
