import React from 'react';
import {PropTypes, ImmutablePropTypes} from 'cs-web-components-externals';
import {SVGIconAndLink} from 'cs-web-components-base';
import * as constants from '../../constants';
import {prefixNS} from '../../helpers';

function ObjectIconAndLink({obj}) {
    if (obj) {
        return (
            <SVGIconAndLink
                data-cdb-object-id={obj.get(constants.CDB_OBJECT_ID)}
                src={obj.get(constants.ICON_LINK)}
                to={obj.get(constants.UI_LINK)}
                label={obj.get(constants.DESCRIPTION)}
                className={prefixNS('svg-icon-and-link')}
            />
        );
    } else {
        return null;
    }
}

ObjectIconAndLink.propTypes = {
    obj: ImmutablePropTypes.contains({
        src: PropTypes.string,
        to: PropTypes.string,
        label: PropTypes.string
    })
};

export default ObjectIconAndLink;
