import React from 'react';
import {shallow} from 'enzyme';
import LongText from '../LongText';

const TEXT = `Lorem ipsum dolor sit amet, consectetur adipisici elit,
sed eiusmod tempor incidunt ut labore et dolore magna aliqua.

Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris
nisi ut aliquid ex ea commodi consequat. Quis aute iure reprehenderit
in voluptate velit esse cillum dolore eu fugiat nulla pariatur.


Excepteur sint obcaecat cupiditat non proident, sunt in culpa qui officia
deserunt mollit anim id est laborum.`;

describe('The LongText component', () => {
    it('with text with line breaks displays the passed text', () => {
        const wrapper = shallow(<LongText text={TEXT} />);
        expect(wrapper.find('pre').length).toBe(1);
        expect(wrapper.text()).toEqual(TEXT);
    });

    it('with no text displays the constant null ("--")', () => {
        const wrapper = shallow(<LongText />);
        expect(wrapper.find('pre').length).toBe(1);
        expect(wrapper.text()).toEqual('--');
    });
});