import React from 'react';
import {shallow} from 'enzyme';
import ObjectIconAndLink from '../ObjectIconAndLink';

describe('The ObjectIconAndLink component', () => {
    it('without an object displays nothing (null)', () => {
        const wrapper = shallow(<ObjectIconAndLink />);
        expect(wrapper.text()).toEqual('');
        expect(wrapper.find('SVGIconAndLink').length).toBe(0);
        expect(wrapper.find('.cs-taskmanager-web-svgiconandlink').length).toBe(0);
    });

    it('with an object displays a valid SVGIconAndLick component', () => {
        const obj = Immutable.Map({
            src: 'testfolder',
            to: 'othertestfolder',
            label: 'testing'
        });

        const wrapper = shallow(<ObjectIconAndLink obj={obj} />);
        expect(wrapper.find('SVGIconAndLink').length).toBe(1);
        expect(wrapper.find('.cs-taskmanager-web-svg-icon-and-link').length).toBe(1);
    });
});
