import React from 'react';
import Immutable from 'immutable';
import {storiesOf, appContainer} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';
import * as constants from '../../../constants';

const TaskReferenceList = Registry.findComponent('cs-taskmanager-web-TaskReferenceList');

const objects = Immutable.List();
const relship = Immutable.Map({
    mappedName: 'Example Relationship'
});
const DEFAULT_PROPS = {objects, relship};

storiesOf('TaskReferenceList', {})
    .addDecorator(appContainer)
    .add('Example Relationship', () => (
        <TaskReferenceList {...DEFAULT_PROPS} objects={Immutable.List([
            Immutable.Map({
                [constants.ICON_LINK]: '/resources/icons/byname/branding_web_favicon',
                [constants.DESCRIPTION]: 'Has files',
                [constants.CLASSNAME]: 'cs_tasks_test_class_olc',
                [constants.FILES]: 1
            }),
            Immutable.Map({
                [constants.ICON_LINK]: '/resources/icons/byname/branding_web_favicon',
                [constants.DESCRIPTION]: 'No files, no operations',
                [constants.CLASSNAME]: 'cs_tasks_test_class_custom'
            }),
            Immutable.Map({
                [constants.ICON_LINK]: '/resources/icons/byname/branding_web_favicon',
                [constants.DESCRIPTION]: 'No files, but operations',
                [constants.CLASSNAME]: 'cs_tasks_test_class_olc'
            })
        ])} />
    ))
    .add('Empty Relationship', () => (
        <TaskReferenceList {...DEFAULT_PROPS} />
    ))
;
