import * as helpers from '../../helpers';

export const FETCH_SETTINGS = helpers.prefixNS('FETCH_SETTINGS');
export const RECEIVED_SETTINGS = helpers.prefixNS('RECEIVED_SETTINGS');
export const SAVE_TAGS = helpers.prefixNS('SAVE_TAGS');
export const SAVE_TAGS_RESPONSE = helpers.prefixNS('SAVE_TAGS_RESPONSE');
export const SAVE_READ_STATUS = helpers.prefixNS('SAVE_READ_STATUS');
export const SAVE_READ_STATUS_RESPONSE = helpers.prefixNS('SAVE_READ_STATUS_RESPONSE');
export const GET_TARGET_STATUSES = helpers.prefixNS('GET_TARGET_STATUSES');
export const TARGET_STATUSES = helpers.prefixNS('TARGET_STATUSES');
export const CHANGE_STATUS = helpers.prefixNS('CHANGE_STATUS');
export const STATUS_CHANGE_IN_FLIGHT = helpers.prefixNS('STATUS_CHANGE_IN_FLIGHT');

export function fetchSettings() {
    return {
        type: FETCH_SETTINGS
    };
}

export function saveTags(task_object_id, tags) {
    return {
        type: SAVE_TAGS,
        payload: {task_object_id, tags}
    };
}

export function saveReadStatus(task_object_id, read_status) {
    return {
        type: SAVE_READ_STATUS,
        payload: {task_object_id, read_status}
    };
}

export function getTargetStatuses(task_object_id) {
    return {
        type: GET_TARGET_STATUSES,
        payload: {task_object_id}
    };
}

export function changeStatus(task_object_id, proceed_to, kwargs, condition) {
    return {
        type: CHANGE_STATUS,
        payload: {task_object_id, proceed_to, kwargs, condition}
    };
}
