import React from 'react';
import Immutable from 'immutable';
import {storiesOf} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';

const IconCell = Registry.findComponent('cs-taskmanager-web-IconCell');

const SUCCESS = Immutable.Map({
    icon: '/resources/icons/byname/csweb_check',
    tooltip: 'Erfolg!'
});

const NO_ICON = Immutable.Map({
    tooltip: 'URL missing...'
});

storiesOf('IconCell', {})
    .add('Success', () => (
        <IconCell value={SUCCESS} />
    ))
    .add('Success isGroup', () => (
        <IconCell value={SUCCESS} isGroup />
    ))
    .add('No Icon', () => (
        <IconCell value={NO_ICON} />
    ))
    .add('No Icon isGroup', () => (
        <IconCell value={NO_ICON} isGroup />
    ))
    .add('None', () => (
        <IconCell />
    ))
    .add('None isGroup', () => (
        <IconCell isGroup />
    ))
;
