import React from 'react';
import {storiesOf} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';

const PrioCell = Registry.findComponent('cs-taskmanager-web-PrioCell');

storiesOf('PrioCell', {})
    .add('High', () => (
        <PrioCell value='cs_tasks_prio_high' />
    ))
    .add('High isGroup', () => (
        <PrioCell value='cs_tasks_prio_high' isGroup />
    ))
    .add('Medium', () => (
        <PrioCell value='cs_tasks_prio_medium' />
    ))
    .add('Medium isGroup', () => (
        <PrioCell value='cs_tasks_prio_medium' isGroup />
    ))
    .add('Low', () => (
        <PrioCell value='cs_tasks_prio_low' />
    ))
    .add('Low isGroup', () => (
        <PrioCell value='cs_tasks_prio_low' isGroup />
    ))
    .add('None', () => (
        <PrioCell />
    ))
    .add('None isGroup', () => (
        <PrioCell isGroup />
    ))
    .add('Unknown', () => (
        <PrioCell value='whatever' />
    ))
    .add('Unknown isGroup', () => (
        <PrioCell value='whatever' isGroup />
    ))
;
