import React from 'react';
import Immutable from 'immutable';
import {storiesOf, appContainer} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';

const ReadStatusCell = Registry.findComponent('cs-taskmanager-web-ReadStatusCell');

const ROW = Immutable.Map({});

// FIXME only record actions, don't dispatch
storiesOf('ReadStatusCell', {})
    .addDecorator(appContainer)
    .add('Read', () => (
        <ReadStatusCell row={ROW} value={1} />
    ))
    .add('Read isGroup', () => (
        <ReadStatusCell row={ROW} value={1} isGroup />
    ))
    .add('Unread', () => (
        <ReadStatusCell row={ROW} value={0} />
    ))
    .add('Unread isGroup', () => (
        <ReadStatusCell row={ROW} value={0} isGroup />
    ))
    .add('None', () => (
        <ReadStatusCell row={ROW} />
    ))
    .add('None isGroup', () => (
        <ReadStatusCell row={ROW} isGroup />
    ))
;
