import React from 'react';
import Immutable from 'immutable';
import {storiesOf} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';

const StatusCell = Registry.findComponent('cs-taskmanager-web-StatusCell');

const MINIMAL_STATUS = Immutable.Map({
    data: Immutable.Map({
        status: 10,
        label: 'Example Status',
        color: '#abcdef',
    })
});
const STATUS = Immutable.Map({
    data: Immutable.Map({
        priority: 20,
        status: 10,
        label: 'Example Status',
        color: '#abcdef',
        attributes: Immutable.Map({
            comment: Immutable.Map({
                mandatory: true,
                label: 'Kommentar'
            }),
            optional: Immutable.Map({
                mandatory: false,
                label: 'Optional',
                value: 'Test'
            })
        })
    })
});
const ICON_STATUS = MINIMAL_STATUS.setIn(
    ['data', 'icon_url'],
    '/resources/icons/byname/csweb_check'
);

storiesOf('StatusCell', {})
    .add('Status', () => (
        <StatusCell value={MINIMAL_STATUS} />
    ))
    .add('Status isGroup', () => (
        <StatusCell value={MINIMAL_STATUS} isGroup />
    ))
    .add('X-Large Status', () => (
        <StatusCell value={STATUS} size='xl' />
    ))
    .add('X-Large Status isGroup', () => (
        <StatusCell value={STATUS} size='xl' isGroup />
    ))
    .add('Icon Status', () => (
        <StatusCell value={ICON_STATUS} />
    ))
    .add('Icon Status isGroup', () => (
        <StatusCell value={ICON_STATUS} isGroup />
    ))
    .add('Large Icon Status', () => (
        <StatusCell value={ICON_STATUS} size='lg' />
    ))
    .add('Large Icon Status isGroup', () => (
        <StatusCell value={ICON_STATUS} size='lg' isGroup />
    ))
;
