import React from 'react';
import Immutable from 'immutable';
import {storiesOf, appContainer} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Registry} from 'cs-web-components-base';

const TagsCell = Registry.findComponent('cs-taskmanager-web-TagsCell');

const ROW = Immutable.Map({});
const TAGS = Immutable.List(['laser', 'tag']);

// FIXME only record actions instead of dispatching them
storiesOf('TagsCell', {})
    .addDecorator(appContainer)
    .add('Tags', () => (
        <TagsCell row={ROW} value={TAGS} />
    ))
    .add('Tags isGroup', () => (
        <TagsCell row={ROW} value={TAGS} isGroup />
    ))
    .add('No Tags', () => (
        <TagsCell row={ROW} value={Immutable.List()} />
    ))
    .add('None', () => (
        <TagsCell row={ROW} />
    ))
;
