App.ComparableArray = Ember.ArrayProxy.extend({
    isEqual: function(b) {
        var a = this;
        var all_a_in_b = a.every(function(aItem) {
            return b.any(function(bItem) {
                return aItem.isEqual(bItem);
            });
        });
        if (!all_a_in_b) {
            return false;
        } else {
            var all_b_in_a = b.every(function(bItem) {
                return a.any(function(aItem) {
                    return aItem.isEqual(bItem);
                });
            });
            return all_b_in_a;
        }
    }
});

