import React, {Component} from 'react';
import {classNames} from 'cs-web-components-externals';
import {getAppSetup} from 'cs-web-components-base';

export default class SearchResultDetails extends Component {

    constructor(props) {
        super(props);
    }

    is152() {
        const versionString = getAppSetup().get('cs-vp-items-components').get('ceVersion');
        return versionString.includes('15.2 Service Level');
    }

    render() {
        const className = classNames(
            'cs-vp-items-components-search-result-details',
            {'cs-vp-items-components-search-result-details--fixed-width': this.is152()}
        );

        return (
            <div className={className}>
                {this.props.children}
            </div>
        );
    }
}
