// jshint esversion:6

import Immutable from 'immutable';
import { ListEntryArticle } from './components';

const names = require('./names.json');


const listdata = Immutable.fromJS({
    ListEntryComponent: ListEntryArticle,
    filterField: {
        callback: filterText => listEntry => listEntry.get('_text').indexOf(filterText) > -1,
        label: 'Optional Filter Label',
        placeholder: 'Bitte Filterbegriff eingeben',
        visible: true
    },
    list : names.names.slice(0, 1500).map((name, idx) => ({_id: idx, _text: name, _selected: false}))
});

export default listdata;
