import {
    fetchNodeChildren, updateTreeNode,
    buildTree, setConfig, updateTreeNodeById,
    resetReplacedNodeById, setReplacedNodeById,
    getParentId, generateNodeId,
    receivedObject, receivedRelship
} from './rest-tree-actions';
import reducer from './rest-tree-reducers';

function containerize(obj, namespace, containerId) {
    const result = {};
    for (const [name, value] of Object.entries(obj)) {
        result[name] = value(containerId, namespace);
    }
    return result;
}

export function actions(namespace, containerId) {
    return containerize({
        fetchNodeChildren, updateTreeNode,
        buildTree, setConfig,
        updateTreeNodeById,
        resetReplacedNodeById,
        setReplacedNodeById
    }, namespace, containerId);
}

export {
    reducer,
    getParentId,
    generateNodeId,
    receivedObject,
    receivedRelship
};
