import Immutable from 'immutable';
import {REST_TREE_SET_CONFIG} from './rest-tree-actions';

const reducer = function(state = Immutable.Map(), action) {
    switch (action.type) {
        case REST_TREE_SET_CONFIG:
            return state.set('rest-config', Immutable.Map(action.config));
        default:
            return state;
    }
};

export default function(containerId) {
    return (state, action) => {
        if (containerId === action.containerId) {
            return reducer(state, action);
        } else {
            return state === undefined ? Immutable.Map() : state;
        }
    };
}
