// jshint esversion:6

import React                          from 'react';
import { Glyphicon }                  from 'react-bootstrap';
import {ImmutablePropTypes}             from 'cs-web-components-externals';

import {
    KEY
} from '../table-constants';

export class TableCellCustom extends React.Component {

    constructor(props) {
        super(props);
    }

    render () {
        const icon = <Glyphicon glyph="star"/>;
        return (
            <div className="table-cell-content">
                <span>{icon}</span>
                <span className="table-cell-text">{this.props.row.get(this.props.col.get(KEY))}</span>
            </div>
        );
    }
}
TableCellCustom.propTypes = {
    col: ImmutablePropTypes.map.isRequired,
    row: ImmutablePropTypes.map.isRequired
};
TableCellCustom.defaultProps = {
    keys: {
    }
};
