import Immutable from 'immutable';

import {SET_IMAGE_URL} from './actions';


export const wiki = (state=Immutable.Map(), action) => {
    switch (action.type) {
        case 'SELECT_TREE_NODE':
            return state.set('searchTerm', action.content).delete('url');
        case SET_IMAGE_URL:
            return state.set('url', action.url);
        default:
            return state;
    }
}

export default wiki;
