import Immutable from 'immutable';

export const config = function(state = Immutable.Map(), action) {
    switch (action.type) {
        case 'SET_CONFIG':
            return action.config;
        default:
            return state;
    }
};

export const context = function(state = {}, action) {
    switch (action.type) {
        case 'SET_CONTEXT':
            return action.context;
        default:
            return state;
    }
};


export const instance = function(state = false, action) {
    switch (action.type) {
        case 'INSTANCE_CREATED':
            return action.payload;
        default:
            return state;
    }
};

export const loading = function(state = false, action) {
    switch (action.type) {
        case 'START_LOADING':
            return true;
        case 'STOP_LOADING':
            return false;
        default:
            return state;
    }
};
