import React from 'react';
import Immutable from 'immutable';
import {PropTypes} from 'cs-web-components-externals';
import {Console, prefixNS} from '../helpers.js';
import TableCatalog from './TableCatalog.jsx';

export default class TableCatalogWrapper extends React.Component {
    componentDidMount() {
        Console.warn('TableCatalogWrapper is deprecated. Please use Catalog Components instead.');
    }

    render() {
        const {tabdefURL, height, ...others} = this.props;
        return (
            <div className={prefixNS('table-catalog-wrapper')}
                 style={{height: `${height}px`}}>
                <TableCatalog
                    {...others}
                    selectedIDs={Immutable.OrderedSet()}
                    catalogTableURL={tabdefURL} />
            </div>
        );
    }
}

TableCatalogWrapper.propTypes = {
    tabdefURL: PropTypes.string.isRequired,
    height: PropTypes.number.isRequired,
};
