import React from 'react';
import {PropTypes, classNames, ImmutablePropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers.js';
import EmptyDetail from './EmptyDetail.jsx';

export default class DetailView extends React.Component {
    render() {
        const {detail, contextObject} = this.props;
        if (contextObject === undefined) {
            const classes = classNames(
                prefixNS('class-search-detail'),
                prefixNS('class-search-detail--empty'));
            return (
                <EmptyDetail className={classes} />
            );
        }
        else {
            return React.cloneElement(
                detail,
                {
                    className: prefixNS('class-search-detail'),
                    contextObject: contextObject
                }
            );
        }
    }
}
DetailView.propTypes = {
    detail: PropTypes.element.isRequired,
    contextObject: ImmutablePropTypes.map
};
