import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import SVGIcon from '../components/SVGIcon';
import {prefixNS} from '../helpers.js';
import {formatStr} from '../i18n.js';

export default function ErrorNotification(props) {
    const files = props.result.files || [];
    return (
        <div className={prefixNS('class-view__extension-results')}>
            {files.map((result, idx) => (
                 <div key={idx}>
                     <SVGIcon name={result.success ? 'csweb_check' : 'csweb_remove'}
                              size='sm' />
                     <span className={prefixNS('class-view__extension-result-message')}>
                         {result.file}
                     </span>
                 </div>
             ))}
             <a href={props.result.ui_link}>
                 {formatStr('op_object_created_show_details')}
             </a>
        </div>
    );
}

ErrorNotification.propTypes = {
    result: PropTypes.object
};
