import React from 'react';
import {Alert} from 'react-bootstrap';
import {prefixNS} from '../helpers.js';
import {formatStr} from '../i18n.js';
import Button from '../components/Button.jsx';

import cis_search_document_primary from '../../../resources/cis_search-document_primary.svg';
import cis_star_success from '../../../resources/cis_star_success.svg';

let body = null;

function getBody() {
    if (body === null) {
        const labels = formatStr('not_yet_searched_body').split('|');
        body = (<React.Fragment>
            {labels[0]}<br />
            {labels[1]}
            <br />
            <Button.IconButton
                title={formatStr('form_name')}
                iconSrc={cis_search_document_primary}
                buttonStyle="primary" />
                {labels[2]}
            <br />
            <Button.IconButton
                title={formatStr('extended_search_favorites')}
                iconSrc={cis_star_success}
                buttonStyle="info" />
                {labels[3]}
        </React.Fragment>);
    }
    return body;
}

export default function NotYetSearched() {
    return (
        <Alert bsStyle="info">
            <div className={prefixNS('not-yet-searched__title')}>
                {formatStr('not_yet_searched_title')}
            </div>
            <div className={prefixNS('not-yet-searched__body')}>
                {getBody()}
            </div>
        </Alert>
    );
}
