import {ACTIONS} from './constants.js';

export function addTabForClass(classname) {
    return {
        type: ACTIONS.ADD_TAB_FOR_CLASS,
        payload: {classname}
    };
}

export function removeTabForClass(classname, instanceName) {
    return {
        type: ACTIONS.REMOVE_TAB_FOR_CLASS,
        payload: {classname, instanceName}
    };
}

export function reOrderTabsForClass(classname, draggedInstanceName, droppedInstanceName) {
    return {
        type: ACTIONS.REORDER_TABS_FOR_CLASS,
        payload: {classname, draggedInstanceName, droppedInstanceName}
    };
}

export function setActiveTab(classname, instanceName, storeSettings = true) {
    return {
        type: ACTIONS.SET_ACTIVE_TAB,
        payload: {classname, instanceName, storeSettings}
    };
}

export function handleSubmit() {
    return {
        type: ACTIONS.HANDLE_SUBMIT,
        payload: {}
    };
}
