import {prefixNS} from '../helpers.js';
import {Registry} from '../registry.js';
import {tabsByClass, currentTabByClass} from './reducers.js';
import './sagas.js';
import ClassApplicationBar from './ClassApplicationBar.jsx';
import ClassView, {ClassViewRaw} from './ClassView.jsx';

Registry.registerReducers({
    tabsByClass,
    currentTabByClass
});

Registry.registerComponent(prefixNS('ClassApplicationBar'), ClassApplicationBar);
Registry.registerComponent(prefixNS('ClassView'), ClassView);

export {
    ClassView,
    ClassViewRaw
};
