import React from 'react';
import {prefixNS} from '../helpers';
import {Badge} from 'react-bootstrap';
import {PropTypes} from 'cs-web-components-externals';

/**
 * Displays the amount of something.
 *
 * React Properties
 * ----------------
 *
 * +------------------+--------------+--------------+-------------------------------------------+
 * | Property         | Type         | Default      | Use                                       |
 * +==================+==============+==============+===========================================+
 * | amount           | number       | \-           | Amount to be displayed.                   |
 * +------------------+--------------+--------------+-------------------------------------------+
 * | tooltip          | string       | \-           | Localized tooltip to explain the          |
 * |                  |              |              | displayed amount.                         |
 * +------------------+--------------+--------------+-------------------------------------------+
 *
 * @class
 */
const AmountBadge = ({amount, tooltip}) => (
    amount !== undefined ? (
        <Badge className={prefixNS('amount-badge')} title={tooltip}>
            {amount}
        </Badge>
    ) : null
);

AmountBadge.propTypes = {
    amount: PropTypes.number,
    tooltip: PropTypes.string
};

export default AmountBadge;
