import React from 'react';
import {classNames, PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers.js';

/**
 * Container component for grouping Button components into a single horizontal bar
 * with small delimiters. Items to group are passed as children.
 *
 * React Properties
 * ----------------
 *
 * +-------------------------+---------+-----------+---------------------------------------------------+
 * | Name                    | Type    | Default   | Description                                       |
 * +=========================+=========+===========+===================================================+
 * | className               | string  | \-        | A custom css className to attach to the component |
 * +-------------------------+---------+-----------+---------------------------------------------------+
 * | withRedundantDropdown   | boolean | true      | true if the last button is a redundant            |
 * |                         |         |           | DropdownButton                                    |
 * +-------------------------+---------+-----------+---------------------------------------------------+
 *
 */
export default class ButtonGroup extends React.Component {

    render() {
        const {children, className, withRedundantDropdown, ...others} = this.props;
        const classes = classNames(
            className,
            prefixNS('button-group'),
            withRedundantDropdown ? prefixNS('withRedundantDropdown') : null
        );
        return (
            <div className={classes}
                 {...others}>
                {children}
            </div>
        );
    }
}
ButtonGroup.propTypes = {
    className: PropTypes.string,
    withRedundantDropdown: PropTypes.bool
};

ButtonGroup.defaultProps = {
    withRedundantDropdown: true
};
