import React from 'react';
import {classNames, PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';

/**
 * Container component for laying out several Button or ButtonGroups components
 * with large delimiters into a single horizontal bar. The button items to be laid out are
 * passed as children.
 *
 * React Properties
 * ----------------
 *
 * +-------------+--------+---------+---------------------------------------------------+
 * | Name        | Type   | Default | Description                                       |
 * +=============+========+=========+===================================================+
 * | className   | string | \-      | A custom css className to attach to the component |
 * +-------------+--------+---------+---------------------------------------------------+
 *
 */
export default class ButtonToolbar extends React.Component {

    render() {
        const {children, className, ...others} = this.props;
        return (
            <div className={classNames(className, prefixNS('button-toolbar'))} {...others}>
                {children}
            </div>
        );
    }
}
ButtonToolbar.propTypes = {
    className: PropTypes.string
};
