import React from 'react';
import {PanelGroup} from 'react-bootstrap';
import {classNames} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';

/**
 * ContentBlockGroup can be used to group several :ref:`cs-web-components-base-components.ContentBlock.__default__`
 * instances. Grouping such elements will lead to an accordion like layout. All panels inside this layout group
 * can be expanded independently. The default expansion state of each component is defined due to its property (see
 * :ref:`cs-web-components-base-components.ContentBlock.__default__`).
 *
 * Examples
 * --------
 * The ContentBlockGroup can be used to group :ref:`cs-web-components-base-components.ContentBlock.__default__`
 * or self defined components based on :ref:`cs-web-components-base-components.ContentBlock.__default__`.
 *
 * Using :ref:`cs-web-components-base-components.ContentBlock.__default__`
 * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 *
 * .. code-block:: none
 *
 *    <ContentBlockGroup>
 *       <ContentBlock ...>
 *          ...
 *       </ContentBlock>
 *       <ContentBlock ...>
 *          ...
 *       </ContentBlock
 *       <ContentBlock ...>
 *          ...
 *       </ContentBlock>
 *    </ContentBlockGroup>
 *
 * Using self defined components
 * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 *
 * If using self defined components, these ones must return an element of type
 * :ref:`cs-web-components-base-components.ContentBlock.__default__`. If so, they can be embedded in ContentBlockGroup
 * as well.
 *
 * .. code-block:: none
 *
 *    <ContentBlockGroup>
 *       <MyComponentX ... />
 *       <MyComponentY ... />
 *       <MyComponentZ ... />
 *    </ContentBlockGroup>
 *
 * React Properties
 * ================
 *
 * +------------------+--------------+--------------+------------------------------------------------------------------+
 * | Property         | Type         | Default      | Use                                                              |
 * +==================+==============+==============+==================================================================+
 * | children         | [components] | \-           | the child components of the ContentBlockGroup                    |
 * |                  |              |              | (only                                                            |
 * |                  |              |              | :ref:`cs-web-components-base-components.ContentBlock.__default__`|
 * |                  |              |              | types are supported)                                             |
 * +------------------+--------------+--------------+------------------------------------------------------------------+
 *
 * @class ContentBlockGroup
 * @extends React.Component
 */
export default function ContentBlockGroup({children, ...others}) {
    const clsName = classNames(others.className, prefixNS('contentblock-group'));
    return (
        <PanelGroup {...others} className={clsName}>
            {children}
        </PanelGroup>
    );
}
