
import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import Button from './Button.jsx';
import {formatStr} from '../i18n.js';

// /docid/en/<id>

/**
* Renders a help button. Clicking the button will open the help page defined by the
* given helpId.
*
* React Properties
* ----------------
*
* +------------------+--------------+--------------+-------------------------------------------+
* | Property         | Type         | Default      | Use                                       |
* +==================+==============+==============+===========================================+
* | helpId           | string       | \-           | The Help-ID of the help page              |
* +------------------+--------------+--------------+-------------------------------------------+
* | helpUrl          | string       | \-           | The URL of the help page if no id is      |
* |                  |              |              | provided                                  |
* +------------------+--------------+--------------+-------------------------------------------+
*
* @class
**/

export function HelpReference(props) {
    const href = props.helpId ? `/docid/de/${props.helpId}` : props.helpUrl;
    return (
        <Button.IconButton
            className='pull-right'
            onClick={() => window.open(href)}
            title={formatStr('help_reference')}
            iconName="csweb_question_sign"
            buttonStyle="info" />
    );
}

HelpReference.propTypes = {
    helpId: PropTypes.string,
    helpUrl: PropTypes.string
};

export default HelpReference;
